import Home from './pages/Home/Home';
import Navigation from './components/shared/Navigation/Navigation';
import Authenticate from './pages/Authenticate/Authenticate';
import { Children } from 'react';
let isAuth = true;

function App() {
    return (
      <BrowserRouter>
        <Navigation/>
        <Routes>
          <Route path = "/" element={
            <GuestRoute>
              <Home/>
            </GuestRoute>
          }/>
          <Route path = "/authenticate" element={
            <GuestRoute>
              <Authenticate/>
            </GuestRoute>
          }/>
          <Route path = "/activate" element={
            <SemiProtectedRoute>
              <Activate/>
            </SemiProtectedRoute>}/>
          <Route path = "/rooms" element={
            <ProtectedRoute>
              <Rooms/>
            </ProtectedRoute>
          }/>
        </Routes>
      </BrowserRouter>
    );
  }


  const GuestRoute  = ({children}) => {
      if(isAuth){
        return (
          <Navigate to="/rooms"/>
        )
      }
      else{
        return children
      }
  }

  const SemiProtectedRoute  = ({children}) => {
      if(!isAuth){
        return (
          <Navigate to="/"/>
        )
      }
      else{
        if(!user.activated){
          return children
        }else{
          return(
            <Navigate to="/rooms"/>
          )
        }
      }
  }

  const ProtectedRoute  = ({children}) => {
      if(!isAuth){
