import axios from 'axios';

export const sendOtp = async (data) => {
  try {
    const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/send-otp`, data);
    return response.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
export default api;
